import groovy.sql.Sql

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver("oracle.jdbc.driver.OracleDriver")
def sql = Sql.newInstance("jdbc:oracle:thin:@br-clan27:1521:cite", "peka", "data")

try {
	sql.connection.autoCommit = false

	sql.execute("DELETE from VERSPEND WHERE VSNUM = 154463")
	sql.execute("DELETE from BOV_VSLOHN WHERE PPROCNR = 0 AND PVSNUM = 154463")

	sql.commit()
} finally {
	if (sql != null) {
		sql.close()
	}
}

