import groovy.sql.Sql

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver("oracle.jdbc.driver.OracleDriver")
def sql = Sql.newInstance("jdbc:oracle:thin:@br-clan27:1521:cite", "peka", "data")

try {
	def vsnum = 154463
	def errors = false;
	def vslohn = sql.firstRow("SELECT * FROM VSLOHN where vsnum = ?", [vsnum])
	if(vslohn == null) {
		log.error("Vslohn not found")
		errors = true
	} else {
		log.info(vslohn)
	}

	def verspend = sql.firstRow("SELECT * FROM VERSPEND WHERE vsnum = ?", [vsnum])
	if(verspend == null) {
		log.error("Verspend not found")
		errors = true
	}

	if(errors) {
		assert false
	}

} finally {
	sql.close();
}

