def sql = Sql.newInstance("jdbc:oracle:thin:@br-clan27:1521:cite", "peka", "data")

try {
	def vsnum = 154463
	def errors = false;
	def vslohn = sql.firstRow("SELECT * FROM VSLOHN where vsnum = ?", [vsnum])

	if(vslohn != null) {
		log.error("Found Vslohn")
		errors = true
	} else {
		log.info("Vslohn deleted")
	}

	def verspend = sql.firstRow("SELECT * FROM VERSPEND WHERE vsnum = ?", [vsnum])
	if(verspend != null) {
		log.error("Found Verspend")
		errors = true
	} else {
		log.info("Verspend deleted")
	}

	if(errors) {
		assert false
	}

} finally {
	sql.close()
}

